import './App.css';

class App extends Component {
    state = {
        count: 0
    };

    constructor() {
        super();
        this.handleIncrement().bind(this);
    }

    handleIncrement = () => {
        state.count = +1;
    }

    render() {
        return (
        <div className={'contenitore'}>
          <button className={'più'} onClick={this.handleIncrement}>+</button>
            <div className={'contatore'}>Contatore = {this.state.count}</div>
          <button className={'meno'}>-</button>
        </div>
      );
    }
}

export default App;
