import React from "react";
import PropTypes from 'prop-types';
import {useState} from "react";

function Contatore (props){
    let { count, increment, decrease, initialState } = props;
    let [ counter, setCounter] = useState(initialState);

    return (
        <div className={'contenitore'}>
            <button className={'più'} onClick={ increment }>+</button>
            <div className={'contatore'}>Contatore = {count}</div>
            <button className={'meno'} onClick={ decrease }>-</button>
        </div>
    );
}

Contatore.propTypes = {
    count: PropTypes.number.isRequired,
    increment: PropTypes.func.isRequired,
    decrease: PropTypes.func.isRequired
}

Contatore.defaultProps = {
    count: 0
}



export default Contatore;
