import './App.css';
import Contatore from './componente/Contatore';
import {useState} from "react";

function App(){
    const [ countOne] = useState(1);
    const [ countHundred, plusOne, minusOne ] = useState(100);

    function increment(event) {
        plusOne(+1);
        plus(+1);
    }

    function decrease(event) {
        minusOne(-1);
        minus(-1);
    }

    return <div>
        <p>Componente contatore da 1</p>
        <Contatore count={ countOne } increment={increment} decrease={decrease} />

        <p>Componente contatore da 100</p>
        <Contatore count={ countHundred } increment={increment}  decrease={decrease}/>
    </div>
}

export default App;
